<?php class UsersController extends Controller {	public function login()	{		if (!empty($_POST['username']) && !empty($_POST['password']))		{						$user = $this->_model->select_user_from_login($_POST);			if ( sizeof($user) > 0)			{				$_SESSION['userrole'] = $user[0]['Userrole']['role'];				switch ($user[0]['Userrole']['role'])				{					case "administrator":						$sendToPage = "../administrators/adminhomepage";					break;					case "root":						$sendToPage = "../roots/homepage";					break;					case "customer":						$sendToPage = "../customers/homepage";					break;				}				$header = "U bent succesvol ingelogd";			}			else			{				$header = "Wachtwoord en/of gebruikersnaam niet juist<br />						   U wordt doorgestuurd naar de homepage";				$sendToPage = "../users/homepage";			}		}		else		{			$header = "U heeft een van de velden niet ingevuld.<br />					   U wordt doorgestuurd naar de Homepage";			$sendToPage = "../users/homepage";		}		$this->set("header", $header);		header("refresh:2;url=".$sendToPage);	}		public function logout()	{		if ( isset($_SESSION['userrole']))		{			session_destroy();			header("location:../users/homepage");		}	}		public function homepage()	{		$this->set('header', 'Welkom bij uw online Assassins Creed the Gathering');	} }?>